package main

import (
	"fmt"

	"github.com/grobelr/rawdecodebtc"
	"github.com/hokaccha/go-prettyjson"
)

const (
	serializedTx  = ""
	serializedTx2 = ""
	serializedTx3 = ""
)

func main() {
	s, err := rawdecodebtc.DecodeRawFromHex(serializedTx, "regtest")
	if err != nil {
		fmt.Println(err)
	}

	p, _ := prettyjson.Marshal(s)
	fmt.Println(string(p))
}
